import React, { useState, useEffect } from 'react';

const ACTIONS = ['View', 'Reply'];

const url = 'api/listings';

const formatAsCurrency = (int) => {
  return '$ ' + int.toFixed(0).replace(/(\d)(?=(\d{3})+(?!\d))/g, '$1,');
};

const Listing = (props) => {
  // TODO
  // This should be the component which renders an individual listing to the page

  const { title, description, imgUrl, price, location } = props.car;

  return (
    <div className='listing'>
      <h3 className='listing__title'>{title}</h3>
      <h3>{formatAsCurrency(price)}</h3>
      <div>{location}</div>
      <img src={imgUrl} alt={title} />
      <p>{description}</p>
      <button>view</button>
      <button>reply</button>
    </div>
  );
};

const Listings = (props) => {
  // TODO
  // This component should make a request to the api endpoint (props.dataEndpoint)
  const [loading, setLoading] = useState(true);
  const [isError, setIsError] = useState(false);

  const [cars, setCars] = useState([]);

  const fetchCars = async () => {
    try {
      const reponse = await fetch(url);
      const newCars = await reponse.json();

      setCars(newCars);
      setLoading(false);
    } catch (err) {
      setLoading(false);
      setIsError(true);
    }
  };
  useEffect(() => {
    fetchCars();
  }, []);
  if (loading) {
    return (
      <section>
        <h1>Loading...</h1>
      </section>
    );
  } else if (isError || !cars) {
    return (
      <section>
        <h1>Something went wrong, please try again later...</h1>
      </section>
    );
  }

  // then render the result as set of listings as per the design mocks
  // check props passed in from parent for other values that you may need to use

  return (
    <div>
      <div className='listings__header'>
        <h2>Search Results</h2>
        <p>
          <span className='text-blue'>{`${cars.length} results `}</span>
          <span>for </span>
          <span className='text-blue'>Ferrari </span>
          <span>in </span>
          <span className='text-blue'>Australia </span>
        </p>
      </div>
      <div className='listings__grid'>
        {cars.map((car, index) => (
          <Listing key={index} car={car} />
        ))}
      </div>
    </div>
  );
};

export default Listings;
